import React from 'react';
import Ieee from '../../assets/img/ieee';
import Corona from '../../assets/img/corona';
import Nss from '../../assets/img/nss';
import Josaa from '../../assets/img/josaa';
import Asme from '../../assets/img/asme';
import Teqip from '../../assets/img/teqip';
import Chanakya from '../../assets/img/chanakya';

const Importantlink=()=>{
    return (
      <div className="link-row">
        <style jsx>{`
          .link-row {
            display: -ms-flexbox;
            display: flex;
            -ms-flex-wrap: wrap;
            flex-wrap: wrap;
            max-width: 100%;
            padding-left: 8%;
            padding-right: 8%;
            margin-top: 5vh;
            margin-bottom: 5vh;
          }
          .link-col {
            position: relative;
            width: 50%;
            display: flex;
            height: 100%;
            justify-content: center;
            padding-top: 2vh;
            padding-bottom: 2vh;
          }
          .implink {
            width: 10vw;
            height: 10vw;
            background: #ffffff;
            box-shadow: 7px 7px 26px rgba(207, 207, 207, 0.36);
            border-radius: 19px;
            margin-left: 1vw;
            display: flex;
            flex-direction: column;
            justify-content: center;
            align-items: center;
            color: #696969;
          }
          .implink > h3 {
            font-size: 1vw;
          }
          .link-col > a {
            text-decoration: none;
          }
          .linkimg {
            margin-top: 10%;
            height: 45%;
            max-width: 70%;
          }
          .implink:hover {
            box-shadow: 0px 0px 28px #d26233;
          }
          @media (max-width: 768px) {
            .link-col {
              width: 100%;
            }
            .implink {
              width: 20vw;
              height: 20vw;
              margin-right: 2vw;
              border-radius: 10px;
            }
          }
        `}</style>
        <div className="link-col">
          <a href="http://exam.nitp.ac.in/">
            <div className="implink">
              <Chanakya />
              <h3>CHANAKYA</h3>
            </div>
          </a>
          <div className="implink">
            <Corona />

            <h3>CORONA</h3>
          </div>
          <div className="implink">
            <Nss />

            <h3>NSS</h3>
          </div>
          <div className="implink">
            <Josaa />

            <h3>JOSAA</h3>
          </div>
        </div>
        <div className="link-col">
          <div className="implink">
            <Asme />

            <h3>ASME</h3>
          </div>
          <div className="implink">
            <Teqip />

            <h3>TEQIP</h3>
          </div>
          <div className="implink">
            <Ieee />

            <h3>IEEE</h3>
          </div>
          <div className="implink">
            <Chanakya />

            <h3>CHANAKYA</h3>
          </div>
        </div>
      </div>
    );
}
export default Importantlink;
